const logNodeError = error => {
  if (error) {
    throw error;
  }
}

const createAndPopulateTempDataTable = (db) => {

  db.serialize(() => {

    db.run('DROP TABLE IF EXISTS TemperatureData', error => {
      if (error) throw error;
    });

    db.run('CREATE TABLE TemperatureData (id INTEGER PRIMARY KEY, location TEXT, year INTEGER NOT NULL, temp_avg REAL NOT NULL)', logNodeError);

    db.run(`INSERT INTO TemperatureData (id, location, year, temp_avg) VALUES
      (1, 'Vostok, Antarctica', 1957, -48.987500000000004),
      (2, 'Capetown, South Africa', 1857, 16.40833333333333),
      (3, 'Capetown, South Africa', 1858, 16.608333333333334),
      (4, 'Capetown, South Africa', 1859, 16.525000000000002),
      (5, 'Capetown, South Africa', 1860, 16.783333333333335),
      (6, 'Capetown, South Africa', 1861, 17.01666666666667),
      (7, 'Capetown, South Africa', 1862, 16.508333333333333),
      (8, 'Capetown, South Africa', 1863, 16.566666666666666),
      (9, 'Capetown, South Africa', 1864, 16.916666666666664),
      (10, 'Capetown, South Africa', 1865, 17.500000000000004),
      (11, 'Capetown, South Africa', 1866, 17.166666666666668),
      (12, 'Capetown, South Africa', 1867, 17.066666666666666),
      (13, 'Capetown, South Africa', 1868, 16.55833333333333),
      (14, 'Capetown, South Africa', 1869, 17.133333333333333)`, logNodeError);

  });

};

module.exports = {createAndPopulateTempDataTable};
