import React from 'react';
import { useState } from 'react';

import Header from '../../components/Header/Header';
import useMultiStepForm from './useMultiStepForm';
import AddTripForm from "./AddTripForm";
import SetGoalForm from "./SetGoalForm";

const INITIAL_DATA = {
    from: '',
    to: '',
    date: '',
    flightClass: '',
    flightNumber: '',
    holdLuggageCheckbox: false,
    holdLuggageWeightAllowance: 0,
    holdLuggageGoal: 0,
    carbonFootprint: 0
}

export default function Form() {

    const [data, setData] = useState(INITIAL_DATA);

    function updateFields(fields) {
        const { name, value, type, checked } = fields.target;
        setData(prev => {
            return {
                ...prev, 
                ...fields, 
                [name]: type === "checkbox" ? checked : value 
            }
        })
    }

    const { steps, currentStepIndex, step, isFirstStep, isLastStep, back, next } = useMultiStepForm([
        <AddTripForm {...data} updateFields={updateFields} />, 
        <SetGoalForm {...data} updateFields={updateFields} />
    ]);

    function onSubmit(e) {
        e.preventDefault();
        if (!isLastStep) return next();
        alert("Succesful submit");
    }

return (
    <div className="add-trip">
        <Header />

        <form onSubmit={onSubmit} className="addtripform">
            <div>{currentStepIndex + 1} / {steps.length}</div>

            {step}

            <div className="buttonsContainer">
                {!isFirstStep && (<button type="button" onClick={back}>Back</button>)}
                <button type="submit">{isLastStep ? "Set Goal" : "Next" }</button>
            </div>
        </form>
    </div>
)}
